import Image from "next/image"
import { cn } from "@/lib/utils"
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card"
import { ShieldCheck, Award, Lightbulb, TrendingUp } from "lucide-react"

interface BenefitTrustItem {
  title: string;
  description: string;
  icon?: React.ElementType; // Lucide icon component
  imageUrl?: string; // For trust badges/logos
}

interface BenefitsTrustProps extends React.HTMLAttributes<HTMLDivElement> {
  sectionTitle?: string;
  sectionDescription?: string;
  items: BenefitTrustItem[];
  variant?: "icon-list" | "trust-badges" | "detailed-benefit"; // Define 3 variations
}

export default function BenefitsTrust({ sectionTitle, sectionDescription, items, variant = "icon-list", className, ...props }: BenefitsTrustProps) {
  return (
    <section className={cn("w-full py-12 md:py-24 lg:py-32", className)} {...props}>
      <div className="container px-4 md:px-6">
        <div className="flex flex-col items-center justify-center space-y-4 text-center mb-8">
          <div className="space-y-2">
            {sectionTitle && <h2 className="text-3xl font-bold tracking-tighter sm:text-5xl">{sectionTitle}</h2>}
            {sectionDescription && <p className="max-w-[900px] text-muted-foreground md:text-xl/relaxed lg:text-base/relaxed xl:text-xl/relaxed">{sectionDescription}</p>}
          </div>
        </div>

        {variant === "icon-list" && (
          <div className="grid gap-8 md:grid-cols-2 lg:grid-cols-3">
            {items.map((item, index) => (
              <Card key={index} className="flex flex-col items-center text-center p-6">
                <CardHeader className="p-0 mb-4">
                  {item.icon && <item.icon className="h-12 w-12 text-primary" />}
                </CardHeader>
                <CardContent className="flex flex-col gap-2 p-0">
                  <CardTitle className="text-xl">{item.title}</CardTitle>
                  <p className="text-muted-foreground">{item.description}</p>
                </CardContent>
              </Card>
            ))}
          </div>
        )}

        {variant === "trust-badges" && (
          <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 lg:grid-cols-5 gap-6 items-center justify-center">
            {items.map((item, index) => (
              <div key={index} className="flex flex-col items-center text-center p-4">
                {item.imageUrl && (
                  <Image
                    src={item.imageUrl}
                    alt={item.title}
                    width={100}
                    height={100}
                    className="object-contain"
                  />
                )}
                <p className="text-sm text-muted-foreground mt-2">{item.title}</p>
              </div>
            ))}
          </div>
        )}

        {variant === "detailed-benefit" && items.length > 0 && (
          <div className="grid md:grid-cols-2 gap-8 items-center max-w-5xl mx-auto">
            <div className="space-y-4">
              <h3 className="text-3xl font-bold">{items[0].title}</h3>
              <p className="text-lg text-muted-foreground">{items[0].description}</p>
            </div>
            {items[0].imageUrl && (
              <Image
                src={items[0].imageUrl}
                alt={items[0].title}
                width={600}
                height={400}
                className="rounded-lg object-cover w-full aspect-video"
              />
            )}
            {items[0].icon && (
                <div className="flex items-center justify-center">
                    <items[0].icon className="h-24 w-24 text-primary" />
                </div>
            )}
          </div>
        )}
      </div>
    </section>
  );
}

import { ShieldCheck, Award, Lightbulb, TrendingUp } from "lucide-react";

export const mockBenefitsTrustData = {
  iconList: {
    sectionTitle: "Why Choose Us?",
    sectionDescription: "Discover the key advantages of our products and services.",
    items: [
      {
        title: "Certified Quality",
        description: "Our products undergo rigorous testing to ensure the highest standards.",
        icon: ShieldCheck,
      },
      {
        title: "Award-Winning Support",
        description: "Dedicated support team available 24/7 to assist you.",
        icon: Award,
      },
      {
        title: "Innovative Solutions",
        description: "Always at the forefront of technology, bringing you cutting-edge features.",
        icon: Lightbulb,
      },
      {
        title: "Proven Growth",
        description: "Join thousands of satisfied customers experiencing significant growth.",
        icon: TrendingUp,
      },
    ],
    variant: "icon-list",
  },
  trustBadges: {
    sectionTitle: "Trusted by Industry Leaders",
    sectionDescription: "Our commitment to excellence is recognized globally.",
    items: [
      {
        title: "Best Product Award 2024",
        description: "Awarded for innovation and user satisfaction.",
        imageUrl: "https://via.placeholder.com/100/FFC107/000000?text=Award+1",
      },
      {
        title: "Certified Partner",
        description: "Official partner with leading technology providers.",
        imageUrl: "https://via.placeholder.com/100/28a745/ffffff?text=Partner",
      },
      {
        title: "100% Secure Transactions",
        description: "Your data and payments are always safe with us.",
        imageUrl: "https://via.placeholder.com/100/007bff/ffffff?text=Secure",
      },
    ],
    variant: "trust-badges",
  },
  detailedBenefit: {
    sectionTitle: "Unmatched Reliability",
    sectionDescription: "Our infrastructure is built for 99.9% uptime, ensuring your services are always available when you need them.",
    items: [
      {
        title: "Unmatched Reliability",
        description: "With redundant systems and continuous monitoring, we guarantee that your services will experience minimal downtime.",
        imageUrl: "https://via.placeholder.com/600x400/17a2b8/ffffff?text=Reliability",
        icon: ShieldCheck,
      },
    ],
    variant: "detailed-benefit",
  },
};
