import { FooterProps } from './types';
import { SimpleFooter } from './variant-simple';
import { MultiColumnFooter } from './variant-multi-column';
import { CenteredFooter } from './variant-centered';
import { CompanyInfoFooter } from './variant-company-info';
import { DarkFooter } from './variant-dark';

export const Footer = (props: FooterProps) => {
    const { variant } = props;

    switch (variant) {
        case 'simple':
            return <SimpleFooter {...props} />;
        case 'multi-column':
            return <MultiColumnFooter {...props} />;
        case 'centered':
            return <CenteredFooter {...props} />;
        case 'company-info':
            return <CompanyInfoFooter {...props} />;
        case 'dark':
            return <DarkFooter {...props} />;
        default:
            return <MultiColumnFooter {...props} />;
    }
};

export default Footer;

export type StrapiImage = {
  url: string;
  alternativeText?: string;
  width?: number;
  height?: number;
};

export type Link = {
  id: number;
  label: string;
  href: string;
  isExternal?: boolean;
};

export type SocialLink = {
  id: number;
  platform: 'facebook' | 'twitter' | 'instagram' | 'linkedin' | 'github' | 'youtube' | 'tiktok';
  url: string;
};

export type FooterColumn = {
  id: number;
  title: string;
  links: Link[];
};

export type ContactInfo = {
  id: number;
  phone?: string;
  email?: string;
  address?: string;
};

export type FooterVariant = 'simple' | 'multi-column' | 'centered' | 'company-info' | 'dark';

export type FooterProps = {
  variant: FooterVariant;
  logo?: {
    data: {
      attributes: StrapiImage;
    };
  };
  description?: string;
  socials?: SocialLink[];
  bottomText?: string;
  columns?: FooterColumn[];
  contacts?: ContactInfo;
  showNewsletter?: boolean;
};

import Link from 'next/link';
import Image from 'next/image';
import { Facebook, Twitter, Instagram, Linkedin, Github, Youtube, Music2, Globe } from 'lucide-react';
import { cn } from '@/lib/utils';
import { SocialLink, StrapiImage } from './types';

export const SocialIcon = ({ platform, className }: { platform: SocialLink['platform']; className?: string }) => {
    const icons = {
        facebook: Facebook,
        twitter: Twitter,
        instagram: Instagram,
        linkedin: Linkedin,
        github: Github,
        youtube: Youtube,
        tiktok: Music2,
    };

    const Icon = icons[platform] || Globe;

    return (
        <div className={cn(
            "p-2 rounded-full bg-secondary/10 hover:bg-primary/10 text-muted-foreground hover:text-primary transition-all duration-300 hover:scale-110",
            className
        )}>
            <Icon className="w-5 h-5" />
        </div>
    );
};

export const FooterLogo = ({ logo, className }: { logo?: { data: { attributes: StrapiImage } }; className?: string }) => {
    if (!logo?.data?.attributes?.url) return null;

    const { url, alternativeText, width, height } = logo.data.attributes;
    // Simple check for relative URL
    const imageUrl = url.startsWith('http') ? url : `${process.env.NEXT_PUBLIC_STRAPI_URL || 'http://localhost:1337'}${url}`;

    return (
        <div className={cn("relative inline-block group", className)}>
            <Image
                src={imageUrl}
                alt={alternativeText || 'Logo'}
                width={width || 120}
                height={height || 40}
                className="object-contain transition-opacity duration-300 group-hover:opacity-80"
            />
        </div>
    );
};

export const FooterBottom = ({ text, className }: { text?: string; className?: string }) => {
    if (!text) return null;
    return (
        <div className={cn("border-t border-border/40 py-8 mt-12 flex flex-col md:flex-row justify-between items-center gap-4 text-sm text-muted-foreground", className)}>
            <p>{text}</p>
            <div className="flex gap-6 text-xs">
                <Link href="#" className="hover:text-foreground transition-colors">Privacy Policy</Link>
                <Link href="#" className="hover:text-foreground transition-colors">Terms of Service</Link>
            </div>
        </div>
    );
};

import Link from 'next/link';
import { FooterProps } from './types';
import { FooterLogo, SocialIcon, FooterBottom } from './shared';

export const SimpleFooter = ({ logo, description, socials, bottomText }: FooterProps) => {
    return (
        <footer className="bg-background border-t border-border/50">
            <div className="container mx-auto px-4 py-16">
                <div className="flex flex-col items-center text-center space-y-8 max-w-2xl mx-auto">
                    <FooterLogo logo={logo} className="scale-110" />

                    {description && (
                        <p className="text-muted-foreground text-lg leading-relaxed">
                            {description}
                        </p>
                    )}

                    {socials && socials.length > 0 && (
                        <div className="flex items-center gap-4">
                            {socials.map((social) => (
                                <Link
                                    key={social.id}
                                    href={social.url}
                                    target="_blank"
                                    rel="noopener noreferrer"
                                    className="transition-transform hover:-translate-y-1"
                                >
                                    <SocialIcon platform={social.platform} />
                                </Link>
                            ))}
                        </div>
                    )}
                </div>

                <FooterBottom text={bottomText} className="mt-16 pt-8 border-t border-border/40" />
            </div>
        </footer>
    );
};

import Link from 'next/link';
import { FooterProps } from './types';
import { FooterLogo, SocialIcon, FooterBottom } from './shared';

export const MultiColumnFooter = ({ logo, description, socials, bottomText, columns }: FooterProps) => {
    return (
        <footer className="bg-background border-t">
            <div className="container mx-auto px-4 py-12">
                <div className="grid grid-cols-1 md:grid-cols-4 gap-8">
                    {/* Brand Column */}
                    <div className="space-y-4">
                        <FooterLogo logo={logo} />
                        {description && (
                            <p className="text-sm text-muted-foreground">
                                {description}
                            </p>
                        )}
                        {socials && socials.length > 0 && (
                            <div className="flex space-x-4 pt-2">
                                {socials.map((social) => (
                                    <Link
                                        key={social.id}
                                        href={social.url}
                                        target="_blank"
                                        rel="noopener noreferrer"
                                        className="text-muted-foreground hover:text-primary transition-colors"
                                    >
                                        <SocialIcon platform={social.platform} />
                                    </Link>
                                ))}
                            </div>
                        )}
                    </div>

                    {/* Links Columns */}
                    {columns?.map((column) => (
                        <div key={column.id} className="space-y-4">
                            <h3 className="font-semibold text-foreground">{column.title}</h3>
                            <ul className="space-y-2">
                                {column.links.map((link) => (
                                    <li key={link.id}>
                                        <Link
                                            href={link.href}
                                            target={link.isExternal ? "_blank" : undefined}
                                            className="text-sm text-muted-foreground hover:text-primary transition-colors"
                                        >
                                            {link.label}
                                        </Link>
                                    </li>
                                ))}
                            </ul>
                        </div>
                    ))}
                </div>

                <FooterBottom text={bottomText} />
            </div>
        </footer>
    );
};

import Link from 'next/link';
import { FooterProps } from './types';
import { FooterLogo, SocialIcon, FooterBottom } from './shared';

export const CenteredFooter = ({ logo, description, socials, bottomText, columns }: FooterProps) => {
    // Flatten links from all columns for the inline menu
    const allLinks = columns?.flatMap(col => col.links) || [];

    return (
        <footer className="bg-background border-t">
            <div className="container mx-auto px-4 py-12">
                <div className="flex flex-col items-center text-center space-y-8">
                    <FooterLogo logo={logo} />

                    {/* Inline Menu */}
                    {allLinks.length > 0 && (
                        <nav>
                            <ul className="flex flex-wrap justify-center gap-6">
                                {allLinks.map((link) => (
                                    <li key={link.id}>
                                        <Link
                                            href={link.href}
                                            target={link.isExternal ? "_blank" : undefined}
                                            className="text-sm font-medium text-muted-foreground hover:text-primary transition-colors"
                                        >
                                            {link.label}
                                        </Link>
                                    </li>
                                ))}
                            </ul>
                        </nav>
                    )}

                    {description && (
                        <p className="text-muted-foreground max-w-2xl">
                            {description}
                        </p>
                    )}

                    {socials && socials.length > 0 && (
                        <div className="flex space-x-6">
                            {socials.map((social) => (
                                <Link
                                    key={social.id}
                                    href={social.url}
                                    target="_blank"
                                    rel="noopener noreferrer"
                                    className="text-muted-foreground hover:text-primary transition-colors"
                                >
                                    <SocialIcon platform={social.platform} className="w-6 h-6" />
                                </Link>
                            ))}
                        </div>
                    )}
                </div>

                <FooterBottom text={bottomText} />
            </div>
        </footer>
    );
};

import Link from 'next/link';
import { Mail, MapPin, Phone } from 'lucide-react';
import { FooterProps } from './types';
import { FooterLogo, SocialIcon, FooterBottom } from './shared';

export const CompanyInfoFooter = ({ logo, description, socials, bottomText, columns, contacts }: FooterProps) => {
    return (
        <footer className="bg-background border-t">
            <div className="container mx-auto px-4 py-12">
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-8">
                    {/* Company Info Column */}
                    <div className="space-y-6">
                        <FooterLogo logo={logo} />
                        {description && (
                            <p className="text-sm text-muted-foreground">
                                {description}
                            </p>
                        )}

                        {contacts && (
                            <div className="space-y-3">
                                {contacts.address && (
                                    <div className="flex items-start space-x-3 text-sm text-muted-foreground">
                                        <MapPin className="w-5 h-5 shrink-0" />
                                        <span>{contacts.address}</span>
                                    </div>
                                )}
                                {contacts.email && (
                                    <div className="flex items-center space-x-3 text-sm text-muted-foreground">
                                        <Mail className="w-5 h-5 shrink-0" />
                                        <a href={`mailto:${contacts.email}`} className="hover:text-primary transition-colors">
                                            {contacts.email}
                                        </a>
                                    </div>
                                )}
                                {contacts.phone && (
                                    <div className="flex items-center space-x-3 text-sm text-muted-foreground">
                                        <Phone className="w-5 h-5 shrink-0" />
                                        <a href={`tel:${contacts.phone}`} className="hover:text-primary transition-colors">
                                            {contacts.phone}
                                        </a>
                                    </div>
                                )}
                            </div>
                        )}

                        {socials && socials.length > 0 && (
                            <div className="flex space-x-4 pt-2">
                                {socials.map((social) => (
                                    <Link
                                        key={social.id}
                                        href={social.url}
                                        target="_blank"
                                        rel="noopener noreferrer"
                                        className="text-muted-foreground hover:text-primary transition-colors"
                                    >
                                        <SocialIcon platform={social.platform} />
                                    </Link>
                                ))}
                            </div>
                        )}
                    </div>

                    {/* Links Columns - Render remaining columns */}
                    <div className="col-span-1 md:col-span-1 lg:col-span-3 grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-8">
                        {columns?.map((column) => (
                            <div key={column.id} className="space-y-4">
                                <h3 className="font-semibold text-foreground">{column.title}</h3>
                                <ul className="space-y-2">
                                    {column.links.map((link) => (
                                        <li key={link.id}>
                                            <Link
                                                href={link.href}
                                                target={link.isExternal ? "_blank" : undefined}
                                                className="text-sm text-muted-foreground hover:text-primary transition-colors"
                                            >
                                                {link.label}
                                            </Link>
                                        </li>
                                    ))}
                                </ul>
                            </div>
                        ))}
                    </div>
                </div>

                <FooterBottom text={bottomText} />
            </div>
        </footer>
    );
};

import Link from 'next/link';
import { FooterProps } from './types';
import { FooterLogo, SocialIcon, FooterBottom } from './shared';
// Removed Shadcn imports to avoid dependency issues in this template
